<script>

	if(performance.navigation.type==1) localStorage.removeItem("ffss")
	

	//JS코드라 주석은 자세하게 안 달거라서 궁금한 건 찾아보거나 연락주면 답변해드릴게요
	document.addEventListener("DOMContentLoaded", function () {
		  // 이건 폰트 사이즈 변경 함수
		  function changeFontSize(fontSize) {
		  			//이것도 수업에 배운 것으로 할려고 했으나 못 찾음
		      	document.querySelectorAll(".baseBox").forEach((e)=>{
						e.style.fontSize = fontSize
					});
					const title = document.getElementById("title");
					title.style.fontSize = (parseInt(fontSize)+12)+"px";
					localStorage.setItem("ffss", fontSize);
		  }

	const savedFontSize = localStorage.getItem("ffss");
	if(savedFontSize){
		changeFontSize(savedFontSize);
	}


			// 라디오 버튼 눌렀을 때 폰트 사이즈 변경
		  //최대한 수업 시간에 배운 거 쓸려고 찾아봤는데 이런 게 있었음
		  document.getElementsByName("fontSize").forEach((font) => {
		      font.addEventListener("click", function () {
		          changeFontSize(this.id);
		      });
		  });

		  // 직접 입력할 때 처리하는 코드
		  const whatIsFontSize = document.getElementById("whatIsFontSize");
		  const fontSizeBtn = document.getElementById("fontSizeBtn");

		  fontSizeBtn.addEventListener("click", function () {
		  		console.log("에베베베");
		      const FS = whatIsFontSize.value.trim();
		      if (FS && !isNaN(FS)) {
		          changeFontSize(FS + "px");
		      } else {
		          alert("올바른 숫자를 입력하세요!");
		      }
		  });
	});
</script>
